import React, { useState } from 'react';
import axios from 'axios';
import './Table_Button.css';

function TableButton({ data }) {
    const [label, setLabel] = useState(data);
    const [editing, setEditing] = useState(false);
    const [showOptions, setShowOptions] = useState(false);

    const handleMouseEnter = () => {
        setShowOptions(true);
    };

    const handleMouseLeave = () => {
        setShowOptions(false);
    };

    const handleEdit = () => {
        setEditing(true);
    };

    const handleInputChange = (event) => {
        setLabel(event.target.value);
    };

    const handleSave = () => {
        // You may update the label in the database here
        // Example: axios.post('/api/update-button-label', { label })

        setEditing(false);
    };

    const handleOpen = () => {
        // Logic to open
        alert('Open clicked');
    };

    return (
        <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
            {editing ? (
                <div>
                    <input type="text" value={label} onChange={handleInputChange} />
                    <button onClick={handleSave}>Save</button>
                </div>
            ) : (
                <button>
                    <span>{label}</span>
                    {showOptions && !editing && (
                        <div className="options-popup">
                            <button onClick={handleEdit}>Edit</button>
                            <button onClick={handleOpen}>Open</button>
                        </div>
                    )}
                </button>
            )}
        </div>
    );

}

export default TableButton;
