import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Profile.css";

function UserCompanyRelationForm() {
    const [data, setData] = useState([]);
    const [newEntry, setNewEntry] = useState({
        companyName: "",
        status: "",
        nextDeadline: "",
        links: "",
    });

    // Fetch initial data
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/profile');
                if (response.data && response.data.userCompanies) {
                    setData(response.data.userCompanies);
                }
            } catch (error) {
                console.error("Error fetching data: ", error);
            }
        };
        fetchData();
    }, []);

    const handleInputChange = (event) => {
        setNewEntry({ ...newEntry, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        await axios.post("/api/addCompanyRow", newEntry);
        setData([...data, newEntry]);
        setNewEntry({
            companyName: "",
            status: "",
            nextDeadline: "",
            links: "",
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            <table>
                <thead>
                    <tr>
                        <th>Company Name</th>
                        <th>Status</th>
                        <th>Next Deadline</th>
                        <th>Links</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((entry, index) => (
                        <tr key={index}>
                            <td>{entry.companyName}</td>
                            <td>{entry.status}</td>
                            <td>{entry.nextDeadline}</td>
                            <td>{entry.links}</td>
                        </tr>
                    ))}
                    <tr>
                        <td>
                            <input
                                type="text"
                                name="companyName"
                                value={newEntry.companyName}
                                onChange={handleInputChange}
                            />
                        </td>
                        <td>
                            <input
                                type="text"
                                name="status"
                                value={newEntry.status}
                                onChange={handleInputChange}
                            />
                        </td>
                        <td>
                            <input
                                type="text"
                                name="nextDeadline"
                                value={newEntry.nextDeadline}
                                onChange={handleInputChange}
                            />
                        </td>
                        <td>
                            <input
                                type="text"
                                name="links"
                                value={newEntry.links}
                                onChange={handleInputChange}
                            />
                        </td>
                    </tr>
                </tbody>
            </table>
            <button type="submit">Add New Entry</button>
        </form>
    );
}

export default UserCompanyRelationForm;
