import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Table_Button.css';

function Table_Button() {
    const [label, setLabel] = useState('Loading...');
    const [editing, setEditing] = useState(false);
    const [showOptions, setShowOptions] = useState(false);

    useEffect(() => {
        // Fetch the button label from the MongoDB database
        axios.get('/api/button-label')
        .then(response => {
            setLabel(response.data.label);
        })
        .catch(error => {
            console.error('An error occurred while fetching the label:', error);
            setLabel('Error loading label');
        });
    }, []);

    const handleMouseEnter = () => {
        setShowOptions(true);
    };

    const handleMouseLeave = () => {
        setShowOptions(false);
    };

    const handleEdit = () => {
        setEditing(true);
    };

    const handleInputChange = (event) => {
        setLabel(event.target.value);
    };

    const handleSave = () => {
        // Update the label in the database
        axios.post('/api/update-button-label', { label })
        .then(() => {
            alert('Label updated successfully!');
        })
        .catch(error => {
            console.error('An error occurred while updating the label:', error);
            alert('An error occurred while updating the label.');
        });

        setEditing(false);
    };

    const handleOpen = () => {
        // Logic to open
        alert('Open clicked');
    };

    return (
        <div onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}>
        {editing ? (
            <div>
            <input type="text" value={label} onChange={handleInputChange} />
            <button onClick={handleSave}>Save</button>
            </div>
        ) : (
            <button>{label}</button>
        )}
        {showOptions && !editing && (
            <div className="options-popup">
            <button onClick={handleEdit}>Edit</button>
            <button onClick={handleOpen}>Open</button>
            </div>
        )}
        </div>
    );
}

export default TableButton;
