import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Profile.css";

function UserCompanyRelationForm() {
    const [data, setData] = useState([]);
    const [newEntry, setNewEntry] = useState({
        companyName: "",
        status: "",
        nextDeadline: "",
        links: "",
    });

    // Fetch initial data
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/profile');
                if (response.data && response.data.userCompanies) {
                    setData(response.data.userCompanies);
                }
            } catch (error) {
                console.error("Error fetching data: ", error);
            }
        };
        fetchData();
    }, []);

    const handleInputChange = (event) => {
        setNewEntry({ ...newEntry, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        await axios.post("http://localhost:3000/api/addCompanyRow", newEntry);
        setData([...data, newEntry]);
        setNewEntry({
            companyName: "",
            status: "",
            nextDeadline: "",
            links: "",
        });
    };

    const onClick = async (event) => {
        event.preventDefault();
    };

    return (
        <div className="data-table">
            <h1>Your Application Process</h1>
            <div className="table">
                <div className="table-header">
                    <div className="header__item"><a id="company_name" className="filter__link" href="#">Company Name</a></div>
                    <div className="header__item"><a id="status" className="filter__link filter__link--number" href="#">Status</a></div>
                    <div className="header__item"><a id="next_deadline" className="filter__link filter__link--number" href="#">Next Deadline</a></div>
                    <div className="header__item"><a id="links" className="filter__link" href="#">Links</a></div>
                </div>
            </div>



        </div>


    );
}

export default UserCompanyRelationForm;
