import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Profile.css";
import Table_Button from "../Buttons/Table_Button";

function UserCompanyRelationForm() {
    const [data, setData] = useState([]);
    const [newEntry, setNewEntry] = useState({
        companyName: "",
        status: "",
        nextDeadline: "",
        links: "",
    });

    // Fetch initial data
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/profile');
                if (response.data && response.data.userCompanies) {
                    setData(response.data.userCompanies);
                }
            } catch (error) {
                console.error("Error fetching data: ", error);
            }
        };
        fetchData();
    }, []);

    const handleInputChange = (event) => {
        setNewEntry({ ...newEntry, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        await axios.post("http://localhost:3000/api/addCompanyRow", newEntry);
        setData([...data, newEntry]);
        setNewEntry({
            companyName: "",
            status: "",
            nextDeadline: "",
            links: "",
        });
    };

    const onClick = async (event) => {
        event.preventDefault();
    };

    return (
        <div className="profile">
            <div className="profile__sidebar">
                <div className="profile__sidebar__title">Filters
                </div>
            </div>
            <div className="profile__body">
                <div className="profile__body__title">Profile Data</div>
                    <div className="profile__body__table">
                        <div className= "profile_body_header">
                            <div className="profile_body_header_item">Company Name</div>
                            <div className="profile_body_header_item">Status</div>
                            <div className="profile_body_header_item">Next Deadline</div>
                            <div className="profile_body_header_item">Links</div>
                            <div className="profile_body_header_item">Actions</div>
                        </div>
                        <div className="profile_body_content">
                            {data.map((row) => (
                                <div className="profile_body_content_row">
                                    <div className="profile_body_content_item">{row.companyName}</div>
                                    <div className="profile_body_content_item">{row.status}</div>
                                    <div className="profile_body_content_item">{row.nextDeadline}</div>
                                    <div className="profile_body_content_item">{row.links}</div>
                                    <div className="profile_body_content_item">
                                        <Table_Button
                                            onClick={onClick}
                                            text="Edit"
                                        />
                                        <Table_Button
                                            onClick={onClick}
                                            text="Delete"
                                        />
                                    </div>
                                </div>
                            ))};
                            </div>

                    </div>
                </div>
            </div>

    );
}

export default UserCompanyRelationForm;
