import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Profile.css";
import Cell from "../Buttons/Cell";
import {
    useTable,
    useBlockLayout,
    useResizeColumns,
    useSortBy,
  } from 'react-table';

const Profile = () => {
    const [data, setData] = useState([]);
    const [newEntry, setNewEntry] = useState({
        companyName: "",
        status: "",
        nextDeadline: "",
        links: "",
    });

    const defaultColumn = {
        minWidth: 100,
        width: 150,
        maxWidth: 200,
        Cell: Cell
    };


    // Fetch initial data
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:8080/api/profile');

                if (response.data && response.data.userCompanies) {
                    setData(response.data.userCompanies);
                }
            } catch (error) {
                console.error("Error fetching data: ", error);
            }
        };
        fetchData();
    }, []);

    const handleInputChange = (event) => {
        setNewEntry({ ...newEntry, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        await axios.post("http://localhost:8080/api/addCompanyRow", newEntry);
        setData([...data, newEntry]);
        setNewEntry({
            companyName: "",
            status: "",
            nextDeadline: "",
            links: "",
        });
    };

    const onClick = async (event) => {
        event.preventDefault();
    };

    return (
        <div className="profile">
            {/* <div className="profile__sidebar">
                <div className="profile__sidebar__title">Filters
                </div>
            </div> */}
            <div className="profile__body">
                <div className="profile__body__title">Profile Data</div>
                    <div className="profile__body__table">
                        <div className= "profile_body_header">
                            <div className="profile_body_header_item">Company Name</div>
                            <div className="profile_body_header_item">Status</div>
                            <div className="profile_body_header_item">Next Deadline</div>
                            <div className="profile_body_header_item">Links</div>
                            <div className="profile_body_header_item">Actions</div>
                        </div>
                        <div className="profile_body_content">
                            {data.map((row) => (
                                <div className="profile_body_content_row">
                                    <TableButton data = {row.company_name}/>
                                    <TableButton data = {row.status}/>
                                    <TableButton data = {row.next_deadline}/>
                                    <TableButton data = {row.links}/>
                                </div>
                            ))}
                            </div>

                    </div>
                </div>
            </div>

    );
}

export default Profile;
