import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Profile.css";
import Table_Button from "../Buttons/Table_Button";

function UserCompanyRelationForm() {
    const [data, setData] = useState([]);
    const [newEntry, setNewEntry] = useState({
        companyName: "",
        status: "",
        nextDeadline: "",
        links: "",
    });

    // Fetch initial data
    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:3000/api/profile');
                if (response.data && response.data.userCompanies) {
                    setData(response.data.userCompanies);
                }
            } catch (error) {
                console.error("Error fetching data: ", error);
            }
        };
        fetchData();
    }, []);

    const handleInputChange = (event) => {
        setNewEntry({ ...newEntry, [event.target.name]: event.target.value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        await axios.post("http://localhost:3000/api/addCompanyRow", newEntry);
        setData([...data, newEntry]);
        setNewEntry({
            companyName: "",
            status: "",
            nextDeadline: "",
            links: "",
        });
    };

    const onClick = async (event) => {
        event.preventDefault();
    };

    return (
        <div className="profile">
            <div className="profile__sidebar">
                <div className="profile__sidebar__title">Profile
                </div>
            </div>
            <div className="profile__body">
                <div className="profile__body__title">User Company Relation Form</div>
                </div>
                </div>

    );
}

export default UserCompanyRelationForm;
